package com.dipitize.app.dipitize.adapter;import android.support.v4.app.Fragment;import android.support.v4.app.FragmentManager;import android.support.v4.app.FragmentPagerAdapter;import com.dipitize.app.dipitize.fragment.MessagesFragment;import com.dipitize.app.dipitize.fragment.ResultFragment;import com.dipitize.app.dipitize.fragment.HomeFragment;public class HomePagerAdapter extends FragmentPagerAdapter {    public HomePagerAdapter(FragmentManager fm) {        super(fm);    }    @Override    public Fragment getItem(int position) {        if (position == 0) {            return new HomeFragment();        } else if (position == 1) {            return new ResultFragment();        } else if (position == 2) {            return new MessagesFragment();        } else {            return null;        }    }    @Override    public int getCount() {        return 3;    }    @Override    public CharSequence getPageTitle(int position) {        if (position == 0) {            return "Home";        } else if (position == 1){            return "Results";        } else if (position == 2){            return "Chat";        } else {            return super.getPageTitle(position);        }    }}